from aiot_lcd1602 import LCD1602
from yolobit import *
button_a.on_pressed = None
button_b.on_pressed = None
button_a.on_pressed_ab = button_b.on_pressed_ab = -1
from yolobit_ir_receiver import *;
from event_manager import *
import time
import sys
import uselect

aiot_lcd1602 = LCD1602()

ir_rx = IR_RX(Pin(pin1.pin, Pin.IN));
ir_rx.start();

event_manager.reset()

def on_event_timer_callback_c_t_o_x_g():
  global command, TVcom, TV, DOOR, light, VOL, CHN
  aiot_lcd1602.clear()
  aiot_lcd1602.move_to(0, 0)
  aiot_lcd1602.putstr((''.join([str(x) for x in ['TV:', TV, ' ', TVcom]])))
  TVcom = ''
  aiot_lcd1602.move_to(0, 1)
  aiot_lcd1602.putstr((''.join([str(x2) for x2 in ['CHN:', CHN, ' VOL:', VOL]])))
  aiot_lcd1602.move_to(11, 0)
  aiot_lcd1602.putstr(('D:' + str(DOOR)))

event_manager.add_timer_event(1000, on_event_timer_callback_c_t_o_x_g)

def read_terminal_input():
  spoll=uselect.poll()        # Set up an input polling object.
  spoll.register(sys.stdin, uselect.POLLIN)    # Register polling object.

  input = ''
  if spoll.poll(0):
    input = sys.stdin.read(1)

    while spoll.poll(0):
      input = input + sys.stdin.read(1)

  spoll.unregister(sys.stdin)
  return input

def on_event_timer_callback_S_r_V_T_L():
  global command, TVcom, TV, DOOR, light, VOL, CHN
  command = read_terminal_input()
  if command == '0':
    display.set_brightness(0)
    display.set_all('#0000ff')
    light = 0
  elif command == '25':
    display.set_brightness(25)
    display.set_all('#0000ff')
    light = 25
  elif command == '50':
    display.set_brightness(50)
    display.set_all('#0000ff')
    light = 50
  elif command == '75':
    display.set_brightness(75)
    display.set_all('#0000ff')
  elif command == '100':
    light = 75
    display.set_brightness(100)
    display.set_all('#0000ff')
    light = 100
  elif command == '5':
    TVcom = 'VOL+'
    if VOL < 100:
      VOL = (VOL if isinstance(VOL, (int, float)) else 0) + 1
  elif command == '13':
    TVcom = 'VOL-'
    if VOL > 0:
      VOL = (VOL if isinstance(VOL, (int, float)) else 0) + -1
  elif command == '10':
    TVcom = 'CHN+'
    if CHN < 100:
      CHN = (CHN if isinstance(CHN, (int, float)) else 0) + 1
  elif command == '8':
    TVcom = 'CHN-'
    if CHN > 0:
      CHN = (CHN if isinstance(CHN, (int, float)) else 0) + -1
  elif command == '9':
    if TV == 'ON':
      TV = 'OFF'
    else:
      TV = 'ON'
  elif command == '16':
    pin9.servo_write(0)
    DOOR = 'L'
  elif command == '18':
    pin9.servo_write(90)
    DOOR = 'U'

event_manager.add_timer_event(100, on_event_timer_callback_S_r_V_T_L)

def on_event_timer_callback_i_v_Z_D_r():
  global command, TVcom, TV, DOOR, light, VOL, CHN
  if TV == 'ON':
    if ir_rx.get_code() == IR_REMOTE_SETUP:
      if TV == 'ON':
        TV = 'OFF'
    elif ir_rx.get_code() == IR_REMOTE_UP:
      TVcom = 'VOL+'
      if VOL < 100:
        VOL = (VOL if isinstance(VOL, (int, float)) else 0) + 1
    elif ir_rx.get_code() == IR_REMOTE_DOWN:
      TVcom = 'VOL-'
      if VOL > 0:
        VOL = (VOL if isinstance(VOL, (int, float)) else 0) + -1
    elif ir_rx.get_code() == IR_REMOTE_RIGHT:
      TVcom = 'CHN+'
      if CHN < 100:
        CHN = (CHN if isinstance(CHN, (int, float)) else 0) + 1
    elif ir_rx.get_code() == IR_REMOTE_LEFT:
      TVcom = 'CHN-'
      if CHN > 0:
        CHN = (CHN if isinstance(CHN, (int, float)) else 0) + -1
    ir_rx.clear_code()
  else:
    if ir_rx.get_code() == IR_REMOTE_1:
      DOOR = 'L'
    if ir_rx.get_code() == IR_REMOTE_3:
      DOOR = 'U'
    if ir_rx.get_code() == IR_REMOTE_SETUP:
      TV = 'ON'
    ir_rx.clear_code()

event_manager.add_timer_event(100, on_event_timer_callback_i_v_Z_D_r)

def on_event_timer_callback_p_j_A_s_l():
  global command, TVcom, TV, DOOR, light, VOL, CHN
  print((''.join([str(x3) for x3 in ['!1:TV:', TV, '#']])), end =' ')
  time.sleep_ms(1000)
  print((''.join([str(x4) for x4 in ['!1:VOL:', VOL, '#']])), end =' ')
  time.sleep_ms(1000)
  print((''.join([str(x5) for x5 in ['!1:CHN:', CHN, '#']])), end =' ')
  time.sleep_ms(1000)
  print((''.join([str(x6) for x6 in ['!1:DOOR:', DOOR, '#']])), end =' ')
  time.sleep_ms(1000)

event_manager.add_timer_event(12000, on_event_timer_callback_p_j_A_s_l)

